INSERT INTO `task_template` VALUES (2,'1-数字 1-100',1,'#!/bin/bash\n\n# 从 1 到 100，每秒打印一个数字\nfor ((i = 1; i <= 100; i++)); do\n    echo \"[$(date +%H:%M:%S)] $i\"\n    sleep 1\ndone\n\necho \"完成：所有数字 1-100 已打印完毕。\"\n','2025-08-06 12:47:57.073','2025-08-12 16:14:49.394','admin','admin','测试脚本'),(3,'2-系统状态检查脚本',1,'#!/bin/bash\n\n#==================================================\n#       简化版 Linux 系统状态检查脚本\n#       输出直接打印到终端，不保存日志\n#==================================================\n\n# 颜色定义（可选，让输出更清晰）\nGREEN=\'\\033[0;32m\'\nYELLOW=\'\\033[1;33m\'\nRED=\'\\033[0;31m\'\nNC=\'\\033[0m\'\n\nlog() { echo -e \"${GREEN}[INFO] $1${NC}\"; }\nwarn() { echo -e \"${YELLOW}[WARN] $1${NC}\"; }\nerror() { echo -e \"${RED}[ERROR] $1${NC}\"; }\n\nseparator() {\n    echo \"----------------------------------------\"\n}\n\n# 1. 系统信息\nseparator\nlog \"系统信息\"\nseparator\necho \"主机名: $(hostname)\"\necho \"操作系统: $(grep \'^PRETTY_NAME=\' /etc/os-release | cut -d\'\"\' -f2 2>/dev/null || echo \'未知\')\"\necho \"内核: $(uname -r)\"\n\n# 2. 系统负载\nseparator\nlog \"系统负载 (1/5/15分钟)\"\nseparator\nuptime | awk -F\'load average:\' \'{print \"负载: \" $2}\'\n\n# 3. CPU 使用率\nseparator\nlog \"CPU 使用率\"\nseparator\ntop -bn1 | grep \"Cpu(s)\" | awk \'{print \"CPU 使用: \" $2 \"%\"}\'\n\n# 4. 内存使用\nseparator\nlog \"内存使用\"\nseparator\nfree -h | awk \'/^Mem:/ {printf \"总内存: %s\\t已用: %s\\t空闲: %s\\n\", $2, $3, $4}\'\n\n# 5. 磁盘使用（仅根分区）\nseparator\nlog \"磁盘使用 (/)\"\nseparator\ndf -h / | awk \'NR==2 {printf \"设备: %s\\t总大小: %s\\t已用: %s (%s)\\n\", $1, $2, $3, $5}\'\n\n# 6. 活跃进程数\nseparator\nlog \"进程统计\"\nseparator\nPROC=$(ps aux | wc -l)\necho \"当前运行进程数: $((PROC - 1))\"\n\n# 7. 网络监听端口（常用服务）\nseparator\nlog \"监听端口 (常见服务)\"\nseparator\nss -tuln | grep \':80\\|:443\\|:22\\|:3306\\|:5432\' || echo \"无常见服务监听\"\n\n# 8. 最近登录与 SSH 尝试\nseparator\nlog \"最近登录与安全\"\nseparator\necho \"最近3次登录:\"\nlast | head -3 | awk \'{print $1, $3, $4, $5}\'\n\nFAILED=$(grep \"Failed password\" /var/log/auth.log 2>/dev/null | wc -l)\nif [ $FAILED -gt 0 ]; then\n    warn \"发现 $FAILED 次 SSH 登录失败，请检查安全！\"\nelse\n    log \"无 SSH 登录失败记录\"\nfi\n\nseparator\nlog \"系统状态检查完成。\"','2025-08-06 12:48:34.435','2025-08-12 16:14:21.982','root','admin','test'),(4,'3-自动优化 Linux 内核参数',2,'#!/bin/bash\n\n# 自动优化 Linux 内核参数\n# 脚本针对 RHEL7\n\ncat >> /usr/lib/sysctl.d/00-system.conf << \'EOF\'\nfs.file-max=65535\nnet.ipv4.tcp_timestamps = 0\nnet.ipv4.tcp_synack_retries = 5\nnet.ipv4.tcp_syn_retries = 5\nnet.ipv4.tcp_tw_recycle = 1\nnet.ipv4.tcp_tw_reuse = 1\nnet.ipv4.tcp_fin_timeout = 30\n#net.ipv4.tcp_keepalive_time = 120\nnet.ipv4.ip_local_port_range = 1024 65535\nkernel.shmall = 2097152\nkernel.shmmax = 2147483648\nkernel.shmmni = 4096\nkernel.sem = 5010 641280 5010 128\nnet.core.wmem_default=262144\nnet.core.wmem_max=262144\nnet.core.rmem_default=4194304\nnet.core.rmem_max=4194304\nnet.ipv4.tcp_fin_timeout = 10\nnet.ipv4.tcp_keepalive_time = 30\nnet.ipv4.tcp_window_scaling = 0\nnet.ipv4.tcp_sack = 0\nEOF\n\nsysctl -p\nsleep 3\necho  \"已经完成优化\"\n','2025-08-06 12:49:39.227','2025-09-06 16:27:02.319','lisi','admin','test'),(6,'切割 Nginx 日志文件',2,'#mkdir  /data/scripts  \n#vim   /data/scripts/nginx_log.sh    \n#!/bin/bash  \n  \n# 切割 Nginx 日志文件(防止单个文件过大,后期处理很困难)   \nlogs_path=\"/usr/local/nginx/logs/\"  \nmv ${logs_path}access.log ${logs_path}access_$(date -d \"yesterday\" +\"%Y%m%d\").log  \nkill -USR1  `cat /usr/local/nginx/logs/nginx.pid`  \n  \n# chmod +x  /data/scripts/nginx_log.sh  \n# crontab  ‐e                    #脚本写完后,将脚本放入计划任务每天执行一次脚本  \n0  1  *  *   *   /data/scripts/nginx_log.sh  ','2025-08-06 12:53:39.020','2025-09-06 16:27:07.841','zhangsan','admin','test'),(9,'查找 Linux 系统中的僵尸进程  ',2,'#!/bin/bash  \n  \n# 查找 Linux 系统中的僵尸进程  \n# awk 判断 ps 命令输出的第 8 列为 Z 是,显示该进程的 PID 和进程命令  \nps aux | awk \'{if($8 == \"Z\"){print $2,$11}}\'  ','2025-08-06 14:45:58.982','2025-09-06 16:27:18.196','admin','admin','查找 Linux 系统中的僵尸进程  '),(10,'获取本机 MAC 地址  ',1,'#!/bin/bash  \n  \n# 获取本机 MAC 地址  \nip a s | awk \'BEGIN{print  \" 本 机 MAC 地 址 信 息 如 下 :\"}/^[0‐9]/{print $2;getline;if($0~/link\\/ether/){print $2}}\' | grep -v lo:  \n# awk 读取 ip 命令的输出,输出结果中如果有以数字开始的行,先显示该行的地 2 列(网卡名称),  \n# 接着使用 getline 再读取它的下一行数据,判断是否包含 link/ether  \n# 如果保护该关键词,就显示该行的第 2 列(MAC 地址)  \n# lo 回环设备没有 MAC,因此将其屏蔽,不显示  ','2025-08-06 16:45:07.990','2025-08-12 16:22:43.774','admin','admin','test'),(11,'统计 Linux 进程相关数量信息   ',1,'#!/bin/bash  \n  \n# 统计 Linux 进程相关数量信息   \nrunning=0  \nsleeping=0  \nstoped=0  \nzombie=0  \n# 在 proc 目录下所有以数字开始的都是当前计算机正在运行的进程的进程 PID  \n# 每个 PID 编号的目录下记录有该进程相关的信息  \nfor pid in /proc/[1‐9]*  \ndo  \n  procs=$[procs+1]  \n  stat=$(awk \'{print $3}\' $pid/stat)  \n# 每个 pid 目录下都有一个 stat 文件,该文件的第 3 列是该进程的状态信息  \n    case $stat in  \n    R)  \n    running=$[running+1]  \n    ;;  \n    T)  \n    stoped=$[stoped+1]  \n    ;;  \n    S)  \n    sleeping=$[sleeping+1]  \n    ;;  \n    Z)  \n     zombie=$[zombie+1]  \n     ;;  \n    esac  \ndone  \necho \"进程统计信息如下\"  \necho \"总进程数量为:$procs\"  \necho \"Running 进程数为:$running\"  \necho \"Stoped 进程数为:$stoped\"  \necho \"Sleeping 进程数为:$sleeping\"  \necho \"Zombie 进程数为:$zombie\"  ','2025-08-06 17:19:10.669','2025-08-12 16:23:10.614','admin','admin','ooo'),(12,' 显示本机 Linux 系统上所有开放的端口列表   ',1,'#!/bin/bash  \n  \n# 显示本机 Linux 系统上所有开放的端口列表   \n  \n# 从端口列表中观测有没有没用的端口,有的话可以将该端口对应的服务关闭,防止意外的攻击可能性  \nss -nutlp | awk \'{print $1,$5}\' | awk -F\"[: ]\" \'{print \"协议:\"$1,\"端口号:\"$NF}\' | grep \"[0‐9]\" | uniq  ','2025-08-08 10:03:54.197','2025-08-12 16:24:35.774','admin','admin','新机器初始化'),(13,'nginx安装配置',3,'---\n- hosts: web   #指定标签组\n  vars:\n    hello: Ansible  #定义变量\n\n  tasks:\n  - name: \"配置nginx源\"\n    yum_repository:\n      name: nginx\n      description: nginx repo\n      baseurl: http://nginx.org/packages/centos/7/$basearch/\n      gpgcheck: no\n      enabled: 1\n  - name: \"安装nginx\"\n    yum:\n      name: nginx\n      state: latest\n  - name: \"编写虚拟主机的配置文件\"\n    copy:\n      src: ./site.conf\n      dest: /etc/nginx/conf.d/site.conf\n  - name: \"创建虚拟主机目录\"\n    file:\n      dest: /var/www/html\n      state: directory\n  - name: \"启动nginx\"\n    service:\n      name: nginx\n      state: started\n  - name:  \"测试nginx页面\"\n    shell: echo \"hello  {{hello}}\" > /var/www/html/index.html\n    #引用变量{{}}表示\n','2025-08-15 10:58:03.845','2025-08-15 10:58:03.845','admin','','nginx安装配置');
